      var r = sb[i] * (1 / 255); var g = sb[i + 1] * (1 / 255); var b = sb[i + 2] * (1 / 255); var
        a = sb[i + 3] * (1 / 255);

      nd = UPNG.quantize.getNearest(root, r, g, b, a);
      inds[i >> 2] = nd.ind; tb[i >> 2] = nd.est.rgba;
    }
