      mi = 0;
    for (var i = 0; i < leafs.length; i++) if (leafs[i].est.L > maxL) { maxL = leafs[i].est.L; mi = i; }
    if (maxL < err) break;
    const node = leafs[mi];

    const s0 = UPNG.quantize.splitPixels(nimg, nimg32, node.i0, node.i1, node.est.e, node.est.eMq255);
    const s0wrong = (node.i0 >= s0 || node.i1 <= s0);
    // console.log(maxL, leafs.length, mi);
    if (s0wrong) { node.est.L = 0; continue; }

    const ln = {
      i0: node.i0, i1: s0, bst: null, est: null, tdst: 0, left: null, right: null,
    }; ln.bst = UPNG.quantize.stats(nimg, ln.i0, ln.i1);
    ln.est = UPNG.quantize.estats(ln.bst);
